/**
 * 认证流程状态类型
 */
export type AuthFlow = "sign-in" | "sign-up" | "verify-email" | "verify-code" | "reset-password" | "set-new-password" | "change-password" | "success";
/**
 * 输入模式类型
 */
export type InputMode = "email-only" | "email-password" | "full-signup" | "verification-code" | "password-reset" | "password-change" | "loading";
/**
 * 错误状态类型
 */
export type ErrorState = null | "EMAIL_NOT_VERIFIED" | "EMAIL_MISMATCH" | "PASSWORD_MISMATCH" | "INVALID_CODE" | "CODE_EXPIRED" | "INVALID_EMAIL" | "ACCOUNT_EXISTS" | "ACCOUNT_NOT_FOUND" | "WRONG_PASSWORD" | "GENERIC_ERROR";
/**
 * 认证流程数据
 */
export interface AuthFlowData {
    email?: string;
    name?: string;
    password?: string;
    confirmPassword?: string;
    code?: string;
    currentPassword?: string;
    newPassword?: string;
    successType?: "password-changed" | "password-reset" | "email-verified" | "sign-up" | "sign-in";
}
/**
 * 认证流程状态
 */
export interface AuthFlowState {
    flow: AuthFlow;
    mode: InputMode;
    error: ErrorState;
    errorMessage?: string;
    data: AuthFlowData;
    loading: boolean;
    oauthLoadingProvider?: string;
    resendCountdown?: number;
    anonymousLoading?: boolean;
}
/**
 * 表单字段配置
 */
export interface FormFieldConfig {
    type: "text" | "email" | "password";
    name: string;
    label: string;
    placeholder?: string;
    autocomplete?: string;
    required?: boolean;
    value?: string;
    error?: string;
    disabled?: boolean;
}
/**
 * 按钮配置
 */
export interface ButtonConfig {
    text: string;
    onClick: () => void | Promise<void>;
    variant?: "primary" | "secondary" | "oauth";
    loading?: boolean;
    disabled?: boolean;
    icon?: string;
    fullWidth?: boolean;
}
/**
 * 代码输入配置
 */
export interface CodeInputConfig {
    length: number;
    value: string;
    onChange: (value: string) => void;
    onComplete?: (code: string) => void;
    error?: boolean;
    disabled?: boolean;
}
/**
 * 头部配置
 */
export interface HeaderConfig {
    title: string;
    subtitle?: string;
    highlightedText?: string;
    showLogo?: boolean;
}
/**
 * 错误消息配置
 */
export interface ErrorMessageConfig {
    message: string;
    type?: "error" | "warning" | "info";
    visible: boolean;
    multiline?: boolean;
}
/**
 * OAuth Provider 配置
 */
export interface OAuthProviderConfig {
    id: string;
    name: string;
    enabled: boolean;
    icon?: string;
}
/**
 * 视图渲染上下文
 */
export interface ViewRenderContext {
    state: AuthFlowState;
    labels: any;
    config: any;
    authClient: any;
    onStateChange: (newState: Partial<AuthFlowState>) => void;
    onAction: (action: string, data?: any) => void | Promise<void>;
}
/**
 * 组件清理函数
 */
export type CleanupFunction = () => void;
/**
 * 组件基类接口
 */
export interface Component {
    element: HTMLElement;
    render(): HTMLElement;
    destroy(): void;
    update?(props: any): void;
}
/**
 * 扩展的标签配置
 */
export interface ExtendedLabels {
    common: {
        orText: string;
        loadingText: string;
        requiredError: string;
        unknownError: string;
        backButton: string;
        continueButton: string;
        confirmButton: string;
    };
    signIn: {
        title: string;
        subtitle: string;
        emailLabel: string;
        emailPlaceholder: string;
        passwordLabel: string;
        passwordPlaceholder: string;
        loginButton: string;
        forgotPassword: string;
        toggleToSignUp: string;
        emailOnlyButton: string;
        signUpPrompt: string;
        guestPrompt: string;
        guestLinkText: string;
    };
    signUp: {
        title: string;
        subtitle: string;
        nameLabel: string;
        emailLabel: string;
        emailPlaceholder?: string;
        passwordLabel: string;
        passwordPlaceholder?: string;
        confirmPasswordLabel: string;
        confirmPasswordPlaceholder?: string;
        signUpButton: string;
        toggleToSignIn: string;
        toggleToSignInLink?: string;
    };
    verifyEmail: {
        title: string;
        subtitle?: string;
        sentTo: string;
        checkInbox: string;
        notReceived: string;
        resendButton: string;
        resendButtonWithCountdown?: string;
        changeEmail: string;
        emailSent: string;
    };
    verifyCode: {
        title: string;
        sentTo: string;
        codeLabel: string;
        verifyButton: string;
        invalidCode: string;
        codeExpired: string;
        resendCode: string;
        resendCountdown: string;
        codeSent: string;
    };
    resetPassword: {
        title: string;
        subtitle: string;
        emailLabel: string;
        sendResetButton: string;
        backToSignIn: string;
        emailSent: string;
        instructions: string;
    };
    setNewPassword: {
        title: string;
        subtitle: string;
        newPasswordLabel: string;
        confirmPasswordLabel: string;
        setPasswordButton: string;
        resendCode?: string;
        resendCountdown?: string;
    };
    changePassword: {
        title: string;
        currentPasswordLabel: string;
        newPasswordLabel: string;
        confirmPasswordLabel: string;
        changeButton: string;
    };
    errors: Partial<Record<AuthErrorCode, string>>;
    oauth: {
        buttonTextPrefix: string;
        googleBtnName: string;
        githubBtnName: string;
        [key: string]: string;
    };
    success: {
        title: string;
        signUpComplete: string;
        signInComplete: string;
        passwordReset: string;
        passwordResetMessage?: string;
        passwordChanged: string;
        passwordChangedMessage: string;
        emailVerified: string;
        emailVerifiedMessage?: string;
        continueButton: string;
        backToSignIn: string;
    };
    /** Iframe OAuth Modal 文案配置（staging + iframe 环境下显示） */
    iframeModal?: {
        /** 标题模板，支持 {provider} 占位符 */
        titleTemplate?: string;
        /** 提示消息模板，支持 {provider} 占位符 */
        messageTemplate?: string;
        /** 按钮文案 */
        actionButton?: string;
    };
}
export type AuthErrorCode = "INVALID_EMAIL" | "INVALID_CODE" | "CODE_EXPIRED" | "PASSWORD_MISMATCH" | "GENERIC_ERROR" | "EMAIL_NOT_VERIFIED" | "EMAIL_MISMATCH" | "USER_NOT_FOUND" | "FAILED_TO_CREATE_USER" | "FAILED_TO_CREATE_SESSION" | "FAILED_TO_UPDATE_USER" | "FAILED_TO_GET_SESSION" | "INVALID_PASSWORD" | "INVALID_EMAIL_OR_PASSWORD" | "SOCIAL_ACCOUNT_ALREADY_LINKED" | "PROVIDER_NOT_FOUND" | "INVALID_TOKEN" | "ID_TOKEN_NOT_SUPPORTED" | "FAILED_TO_GET_USER_INFO" | "USER_EMAIL_NOT_FOUND" | "PASSWORD_TOO_SHORT" | "PASSWORD_TOO_LONG" | "USER_ALREADY_EXISTS" | "USER_ALREADY_EXISTS_USE_ANOTHER_EMAIL" | "EMAIL_CAN_NOT_BE_UPDATED" | "CREDENTIAL_ACCOUNT_NOT_FOUND" | "SESSION_EXPIRED" | "FAILED_TO_UNLINK_LAST_ACCOUNT" | "ACCOUNT_NOT_FOUND" | "USER_ALREADY_HAS_PASSWORD" | (string & {});
